    public partial class TestImage : Form
    {
        public TestImage()
        {
            InitializeComponent();
            loadImage(720,925,720,500);
        }

        private void loadImage(int _width, int _height, int _canvasWidth = 0, int _canvasHeight = 0)
        {
            int width = _width, height = _height, bitmapWidth = _canvasWidth, bitmapHeight = _canvasHeight;
            byte[] bytes = Convert.FromBase64String("");
            using (var ms = new MemoryStream(bytes))
            {
                var image = Image.FromStream(ms);

                var ratioX = (double)width / image.Width;
                var ratioY = (double)height / image.Height;
                var ratio = Math.Min(ratioX, ratioY);

                width = (int)(image.Width * ratio);
                height = (int)(image.Height * ratio);

                //var newImage = new Bitmap(bitmapWidth, bitmapHeight);
                Size size = new Size(width, height);
                var newImage = new Bitmap(image, size);
                //Graphics.FromImage(newImage).DrawImage(image, 0, 0, width, height);
                //Bitmap bmp = new Bitmap(newImage);
                
                pictureBox1.Image = newImage;
            }
            
        }

        private void button1_Click(object sender, EventArgs e)
        {
            loadImage(Int32.Parse(txtWidth.Text), Int32.Parse(txtHeight.Text));
        }
    }
}
